// Build "Sumber: <NamaDokumen> [p:x]" using retrieval metadata (Input 2)
const answer = $json.output ?? $json.text ?? '';

// Get items from second input (Retrieval output)
const input2 = $input.all(1) || [];
const collected = [];

// Helper: collect documents array from various shapes
function docsFrom(obj) {
  if (!obj) return [];
  if (Array.isArray(obj)) return obj;
  if (obj.documents) return obj.documents;
  if (obj.results) return obj.results;
  if (obj.matches) return obj.matches;
  if (obj.data?.documents) return obj.data.documents;
  return [];
}

// Aggregate docs
for (const it of input2) {
  const j = it.json ?? it;
  collected.push(...docsFrom(j));
}

const byFile = new Map();
for (const d of collected) {
  const meta = d.metadata || {};
  const name = meta.fileName || meta.filename || meta.file || 'Dokumen';
  const p = meta.pageNumber ?? meta.page ?? null;
  if (!byFile.has(name)) byFile.set(name, new Set());
  if (p != null) byFile.get(name).add(Number(p));
}

// Compress pages into ranges
function compress(pages) {
  const arr = [...pages].sort((a,b)=>a-b);
  const out = [];
  let s = null, prev = null;
  for (const x of arr) {
    if (s === null) { s = prev = x; continue; }
    if (x === prev + 1) { prev = x; continue; }
    out.push(s === prev ? `[p:${s}]` : `[p:${s}‚Äì${prev}]`);
    s = prev = x;
  }
  if (s !== null) out.push(s === prev ? `[p:${s}]` : `[p:${s}‚Äì${prev}]`);
  return out.join(', ');
}

const parts = [];
for (const [name, pages] of byFile.entries()) {
  const tag = (pages && pages.size) ? `${name} ${compress(pages)}` : name;
  parts.push(tag);
}

// Remove any existing citation line that starts with "Sumber:"
let out = answer.replace(/\n?Sumber:\s*.*$/is, '').trim();
if (parts.length) out += `\n\nSumber: ${parts.slice(0,3).join('; ')}`;

return [{ json: { ...$json, output: out, citations: parts } }];

// Telegram Chunker (Code)
// - Konversi Markdown ringan (**bold**, __italic__, `code`) ke HTML
// - Escape karakter berbahaya
// - Potong jadi <= 3500 char (batas aman Telegram 4096)

const MAX = 3500;
let text = ($json.output ?? $json.text ?? '');
const chatId = ($json.chatId ?? $json.message?.chat?.id ?? $json.chat?.id);

// 1) Markdown -> HTML (yang kita pakai saja)
text = text
  .replace(/\*\*(.+?)\*\*/g, '<b>$1</b>')
  .replace(/__(.+?)__/g, '<i>$1</i>')
  .replace(/`([^`]+)`/g, '<code>$1</code>');

// 2) Escape HTML, tapi pertahankan tag <b>, <i>, <code>
const keep = (s) => s.replace(/<(\/?)(b|i|code)>/g, '¬ßTAG$1$2¬ß');
const unkeep = (s) => s.replace(/¬ßTAG(\/?)(b|i|code)¬ß/g, '<$1$2>');
const escapeHtml = (s) => s.replace(/&/g,'&amp;').replace(/</g,'&lt;').replace(/>/g,'&gt;');
text = unkeep(escapeHtml(keep(text)));

// 3) Split pintar
function splitSmart(s, maxLen) {
  const out = [];
  let rest = String(s);
  while (rest.length > maxLen) {
    let cut = rest.lastIndexOf('\n', maxLen);
    if (cut < maxLen * 0.6) cut = rest.lastIndexOf(' ', maxLen);
    if (cut < 0) cut = maxLen;
    out.push(rest.slice(0, cut));
    rest = rest.slice(cut);
  }
  if (rest.length) out.push(rest);
  return out.map(t => t.trim()).filter(Boolean);
}
const chunks = splitSmart(text, MAX);

return chunks.map((c, i) => ({
  json: {
    ...$json,
    chatId,
    output: c + (i < chunks.length - 1 ? `\n\n(terpotong ‚Ä¢ bagian ${i+1}/${chunks.length})` : '')
  }
}));

// Build readable Markdown from Unstructured response
function toMd(el) {
  const type = (el.type || '').toLowerCase();
  const text = (el.text || '').trim();
  const page = el.metadata?.page_number;
  const pg = page ? ` [p:${page}]` : '';
  if (!text) return '';
  if (type.includes('title') || type.includes('header')) return `\n\n## ${text}${pg}\n`;
  if (type.includes('list')) return `- ${text}${pg}`;
  if (type.includes('table')) return `\n\n${text}${pg}\n`;
  return `${text}${pg}`;
}

return items.map(item => {
  const payload = item.json;
  const elements = Array.isArray(payload) ? payload : (payload?.elements || []);
  const lines = [];
  let lastWasList = false;
  for (const el of elements) {
    const md = toMd(el);
    if (!md) continue;
    if (md.startsWith('- ')) {
      if (!lastWasList) lines.push('');
      lines.push(md);
      lastWasList = true;
    } else {
      if (lastWasList) lines.push('');
      lines.push(md);
      lastWasList = false;
    }
  }
  const text = lines.join('\n').replace(/\n{3,}/g, '\n\n').trim();
  return { json: { text, fileName: (item.json?.metadata?.filename || 'ocr.txt') } };
});

// Convert $json.text into a TXT file in binary property 'data'
const text = String($json.text || '');
const buf = Buffer.from(text, 'utf-8');
const binary = await this.helpers.prepareBinaryData(buf, ($json.fileName || 'ocr.txt'), 'text/plain');
return [{ json: { fileName: ($json.fileName || 'ocr.txt') }, binary: { data: binary } }];

// Enhanced Project Update Command Detector & Parser
const item = $input.first();
const message = item.json?.message?.text || '';

// Enhanced project update patterns detection
const updatePatterns = [
    // Progress update patterns
    {
        pattern: /(?:update|progress|kemajuan)\s+(?:proyek|project)\s+([^\n]+?)\s+(?:progress|kemajuan)\s+(\d+)%?/i,
        type: 'progress'
    },
    {
        pattern: /(?:proyek|project)\s+([^\n]+?)\s+(?:progress|kemajuan|update)\s+(\d+)%?/i,
        type: 'progress_percent'
    },
    // Status update patterns
    {
        pattern: /(?:status|kondisi)\s+(?:proyek|project)\s+([^\n]+?)\s*[:=]\s*([^\n]+)/i,
        type: 'status'
    },
    // Material update patterns  
    {
        pattern: /(?:material|bahan)\s+(?:proyek|project)\s+([^\n]+?)\s*[:=]\s*([^\n]+)/i,
        type: 'material'
    },
    // Equipment update patterns
    {
        pattern: /(?:equipment|alat)\s+(?:proyek|project)\s+([^\n]+?)\s*[:=]\s*([^\n]+)/i,
        type: 'equipment'
    },
    // Resource update patterns
    {
        pattern: /(?:resource|sumber\s*daya)\s+(?:proyek|project)\s+([^\n]+?)\s*[:=]\s*([^\n]+)/i,
        type: 'resource'
    }
];

let projectName = '';
let updateType = '';
let updateValue = '';
let confidence = 0;

// Check each pattern
for (let patternObj of updatePatterns) {
    const match = message.match(patternObj.pattern);
    if (match) {
        updateType = patternObj.type;

        if (match.length >= 3) {
            projectName = match[1].trim();
            updateValue = match[2].trim();
        }

        confidence = 0.9;
        break;
    }
}

// Check for project list command
if (message.toLowerCase().includes('list proyek') || 
    message.toLowerCase().includes('daftar proyek') ||
    message.toLowerCase().includes('show projects')) {
    return [{
        json: {
            ...item.json,
            isListCommand: true,
            sheetId: "1d44mV-M5SBC23bIb5-hs0ZgUXhnFS4J7C_1o2RkDIU"
        }
    }];
}

// Get current date for timestamp
const currentDate = new Date();
const todayColumn = currentDate.getDate() + '-' + currentDate.toLocaleString('default', { month: 'short' });

const result = {
    isProjectUpdate: confidence > 0.5,
    projectName: projectName,
    updateType: updateType,
    updateValue: updateValue,
    confidence: confidence,
    dateColumn: todayColumn,
    timestamp: currentDate.toISOString(),
    originalMessage: message,
    chatId: item.json?.message?.chat?.id,
    userId: item.json?.message?.from?.id,
    userName: item.json?.message?.from?.first_name || 'User',
    sheetId: "1d44mV-M5SBC23bIb5-hs0ZgUXhnFS4J7C_1o2RkDIU"
};

return [{ 
    json: {
        ...item.json,
        projectUpdate: result
    } 
}];

// Prepare Update Data for Google Sheets
const lookupItem = $input.first();
const lookupRow = lookupItem?.json ?? {};
const projectUpdate = $items('Project Command Detector')[0]?.json?.projectUpdate ?? lookupRow.projectUpdate ?? {};

// Map update types to column names
const columnMapping = {
    'progress': 'STATUS',
    'progress_percent': 'STATUS',
    'material': 'REMARK',
    'equipment': 'REMARK',
    'resource': 'REMARK',
    'status': 'STATUS'
};

// Get the appropriate column for update
const targetColumn = columnMapping[projectUpdate.updateType] || 'REMARK';

// Prepare the update value
let finalUpdateValue = projectUpdate.updateValue ?? '';
if (['progress', 'progress_percent'].includes(projectUpdate.updateType) && projectUpdate.updateValue) {
    finalUpdateValue = `Progress ${projectUpdate.updateValue}${String(projectUpdate.updateValue).includes('%') ? '' : '%'}`;
}

// Get current date formatted
const currentDate = new Date();
const dateColumn = `${currentDate.getDate()}-${currentDate.toLocaleString('en', { month: 'short' })}`;

return [{
    json: {
        ...lookupRow,
        projectUpdate,
        updateData: {
            targetColumn,
            updateValue: finalUpdateValue,
            dateColumn,
            dateValue: currentDate.toLocaleDateString('id-ID')
        }
    }
}];

